(139, 'theme_mods_hestia', 'a:7:{i:0;b:0;s:18:\"custom_css_post_id\";i:-1;s:18:\"nav_menu_locations\";a:2:{s:7:\"primary\";i:2;s:6:\"footer\";i:3;}s:18:\"hestia_page_editor\";s:318:\"<div class=\"col-md-5\"><h3>About Hestia</h3>Need more details? Please check our full documentation for detailed information on how to use Hestia.</div><div class=\"col-md-6 col-md-offset-1\"><img class=\"size-medium alignright\" src=\"http://localhost/triptogo/wp-content/themes/hestia/assets/img/about-content.png\" /></div>\";s:24:\"hestia_feature_thumbnail\";s:73:\"http://localhost/triptogo/wp-content/themes/hestia/assets/img/contact.jpg\";s:11:\"custom_logo\";i:9;s:12:\"accent_color\";s:7:\"#37c8ab\";}', 'yes'),
